import React, {useEffect, useRef, useState} from "react";
import axios from "axios";
import "./style/Service.scss";
import ReactLoading from 'react-loading'
import rightArrow from "../../asset/images/rightArrow.png";

function Service(props) {

    const [isLoading, setIsLoading] = useState(false);
    const [textValue, setTextValue] = useState("");
    const [summarizedText,setSummarizedText] = useState('');


    const handleButtonClick = async () => {
        await axios
            .post("http://127.0.0.1:5000/summary", {text: textValue})
            .then((response) => {
                console.log(response)
                setSummarizedText(response);
                return;
            })
            .catch((error) => {
                console.error(error);
            })
            .finally(() => setIsLoading(false))
    };

    const onTypeEnter = (e) => {
        e.preventDefault();
        if (e.keyCode === 13 && textValue !== '') {
            setIsLoading(true)
            handleButtonClick();
        }
    };

    return (
        <div id={"Service"}>
            {
                isLoading &&
                <div className={'loading-wrapper'}>
                    <div className={'loading'}>
                        <ReactLoading type={'bubbles'} color={'#8968ef'}/>
                    </div>
                </div>
            }

            <div className="main-content-wrapper">
                <div className="user-text-wrapper">
                    <div className="title">
                        User Input
                    </div>
                    <input
                        type="text"
                        className="user-input"
                        placeholder={'요약할 글을 입력해 보세요'}
                        value={textValue}
                        onChange={(e) => setTextValue(e.target.value)}
                        onKeyUp={(e) => onTypeEnter(e)}
                    />
                </div>
                <div className="arrow-wrapper">
                    <img className='right-arrow-img' alt={""} src={rightArrow}/>
                </div>
                <div className="model-text-wrapper">
                    <div className="title">
                        Model Output
                    </div>
                    {
                        summarizedText !== "" ?
                            <div className="model-text">
                                {summarizedText}
                            </div>
                            :
                            <div className="model-text">
                                none
                            </div>
                    }
                </div>
            </div>
        </div>
    );
}

export default Service;
